import React from "react";
import { render } from "react-dom";
import { ResponsiveBar } from "@nivo/bar";
const data = [
  {
    country: "AD",
    "hot dog": 174,
    "hot dogColor": "hsl(21, 70%, 50%)",
    burger: 18,
    burgerColor: "hsl(194, 70%, 50%)",
    sandwich: 199,
    sandwichColor: "hsl(104, 70%, 50%)",
    kebab: 83,
    kebabColor: "hsl(59, 70%, 50%)",
    fries: 150,
    friesColor: "hsl(289, 70%, 50%)",
    donut: 129,
    donutColor: "hsl(269, 70%, 50%)",
  },
  {
    country: "AE",
    "hot dog": 15,
    "hot dogColor": "hsl(297, 70%, 50%)",
    burger: 109,
    burgerColor: "hsl(121, 70%, 50%)",
    sandwich: 9,
    sandwichColor: "hsl(30, 70%, 50%)",
    kebab: 177,
    kebabColor: "hsl(357, 70%, 50%)",
    fries: 193,
    friesColor: "hsl(355, 70%, 50%)",
    donut: 44,
    donutColor: "hsl(336, 70%, 50%)",
  },
  {
    country: "AF",
    "hot dog": 121,
    "hot dogColor": "hsl(277, 70%, 50%)",
    burger: 157,
    burgerColor: "hsl(254, 70%, 50%)",
    sandwich: 178,
    sandwichColor: "hsl(100, 70%, 50%)",
    kebab: 157,
    kebabColor: "hsl(129, 70%, 50%)",
    fries: 106,
    friesColor: "hsl(319, 70%, 50%)",
    donut: 152,
    donutColor: "hsl(201, 70%, 50%)",
  },
  {
    country: "AG",
    "hot dog": 146,
    "hot dogColor": "hsl(296, 70%, 50%)",
    burger: 61,
    burgerColor: "hsl(297, 70%, 50%)",
    sandwich: 55,
    sandwichColor: "hsl(26, 70%, 50%)",
    kebab: 199,
    kebabColor: "hsl(180, 70%, 50%)",
    fries: 72,
    friesColor: "hsl(86, 70%, 50%)",
    donut: 54,
    donutColor: "hsl(9, 70%, 50%)",
  },
  {
    country: "AI",
    "hot dog": 89,
    "hot dogColor": "hsl(257, 70%, 50%)",
    burger: 113,
    burgerColor: "hsl(136, 70%, 50%)",
    sandwich: 47,
    sandwichColor: "hsl(261, 70%, 50%)",
    kebab: 110,
    kebabColor: "hsl(221, 70%, 50%)",
    fries: 41,
    friesColor: "hsl(175, 70%, 50%)",
    donut: 167,
    donutColor: "hsl(189, 70%, 50%)",
  },
  {
    country: "AL",
    "hot dog": 149,
    "hot dogColor": "hsl(117, 70%, 50%)",
    burger: 58,
    burgerColor: "hsl(297, 70%, 50%)",
    sandwich: 149,
    sandwichColor: "hsl(77, 70%, 50%)",
    kebab: 173,
    kebabColor: "hsl(350, 70%, 50%)",
    fries: 121,
    friesColor: "hsl(263, 70%, 50%)",
    donut: 121,
    donutColor: "hsl(49, 70%, 50%)",
  },
  {
    country: "AM",
    "hot dog": 139,
    "hot dogColor": "hsl(38, 70%, 50%)",
    burger: 23,
    burgerColor: "hsl(193, 70%, 50%)",
    sandwich: 38,
    sandwichColor: "hsl(309, 70%, 50%)",
    kebab: 56,
    kebabColor: "hsl(193, 70%, 50%)",
    fries: 12,
    friesColor: "hsl(157, 70%, 50%)",
    donut: 138,
    donutColor: "hsl(275, 70%, 50%)",
  },
];
const App = () => {
  return (
    <div style={{ width: "800px", height: "800px" }}>
      <ResponsiveBar
        data={data}
        keys={["hot dog", "burger", "sandwich", "kebab", "fries", "donut"]}
        indexBy="country"
        margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
        padding={0.3}
        colors={{ scheme: "nivo" }}
        defs={[
          {
            id: "dots",
            type: "patternDots",
            background: "inherit",
            color: "#38bcb2",
            size: 4,
            padding: 1,
            stagger: true,
          },
          {
            id: "lines",
            type: "patternLines",
            background: "inherit",
            color: "#eed312",
            rotation: -45,
            lineWidth: 6,
            spacing: 10,
          },
        ]}
        fill={[
          {
            match: {
              id: "fries",
            },
            id: "dots",
          },
          {
            match: {
              id: "sandwich",
            },
            id: "lines",
          },
        ]}
        borderColor={{ from: "color", modifiers: [["darker", 1.6]] }}
        axisTop={null}
        axisRight={null}
        axisBottom={{
          tickSize: 5,
          tickPadding: 5,
          tickRotation: 0,
          legend: "country",
          legendPosition: "middle",
          legendOffset: 32,
        }}
        axisLeft={{
          tickSize: 5,
          tickPadding: 5,
          tickRotation: 0,
          legend: "food",
          legendPosition: "middle",
          legendOffset: -40,
        }}
        labelSkipWidth={12}
        labelSkipHeight={12}
        labelTextColor={{ from: "color", modifiers: [["darker", 1.6]] }}
        legends={[
          {
            dataFrom: "keys",
            anchor: "bottom-right",
            direction: "column",
            justify: false,
            translateX: 120,
            translateY: 0,
            itemsSpacing: 2,
            itemWidth: 100,
            itemHeight: 20,
            itemDirection: "left-to-right",
            itemOpacity: 0.85,
            symbolSize: 20,
            effects: [
              {
                on: "hover",
                style: {
                  itemOpacity: 1,
                },
              },
            ],
          },
        ]}
        animate={true}
        motionStiffness={90}
        motionDamping={15}
      />
    </div>
  );
};
render(<App />, document.querySelector("#content"));
