import React, { useEffect, useState } from "react";
import { render } from "react-dom";
import { ResponsiveBar } from "@nivo/bar";

const App = () => {
  const data = [
    {
      country: "AD",
      "hot dog": 25,
      "hot dogColor": "hsl(88, 70%, 50%)",
      burger: 97,
      burgerColor: "hsl(0, 70%, 50%)",
      sandwich: 193,
      sandwichColor: "hsl(43, 70%, 50%)",
      kebab: 28,
      kebabColor: "hsl(305, 70%, 50%)",
      fries: 63,
      friesColor: "hsl(95, 70%, 50%)",
      donut: 194,
      donutColor: "hsl(11, 70%, 50%)",
    },
    {
      country: "AE",
      "hot dog": 4,
      "hot dogColor": "hsl(54, 70%, 50%)",
      burger: 95,
      burgerColor: "hsl(256, 70%, 50%)",
      sandwich: 150,
      sandwichColor: "hsl(233, 70%, 50%)",
      kebab: 8,
      kebabColor: "hsl(137, 70%, 50%)",
      fries: 65,
      friesColor: "hsl(90, 70%, 50%)",
      donut: 38,
      donutColor: "hsl(248, 70%, 50%)",
    },
    {
      country: "AF",
      "hot dog": 123,
      "hot dogColor": "hsl(182, 70%, 50%)",
      burger: 87,
      burgerColor: "hsl(188, 70%, 50%)",
      sandwich: 5,
      sandwichColor: "hsl(179, 70%, 50%)",
      kebab: 51,
      kebabColor: "hsl(306, 70%, 50%)",
      fries: 137,
      friesColor: "hsl(161, 70%, 50%)",
      donut: 117,
      donutColor: "hsl(4, 70%, 50%)",
    },
    {
      country: "AG",
      "hot dog": 0,
      "hot dogColor": "hsl(278, 70%, 50%)",
      burger: 1,
      burgerColor: "hsl(191, 70%, 50%)",
      sandwich: 84,
      sandwichColor: "hsl(299, 70%, 50%)",
      kebab: 23,
      kebabColor: "hsl(166, 70%, 50%)",
      fries: 108,
      friesColor: "hsl(47, 70%, 50%)",
      donut: 99,
      donutColor: "hsl(126, 70%, 50%)",
    },
    {
      country: "AI",
      "hot dog": 67,
      "hot dogColor": "hsl(324, 70%, 50%)",
      burger: 146,
      burgerColor: "hsl(302, 70%, 50%)",
      sandwich: 199,
      sandwichColor: "hsl(34, 70%, 50%)",
      kebab: 195,
      kebabColor: "hsl(120, 70%, 50%)",
      fries: 51,
      friesColor: "hsl(45, 70%, 50%)",
      donut: 59,
      donutColor: "hsl(276, 70%, 50%)",
    },
    {
      country: "AL",
      "hot dog": 93,
      "hot dogColor": "hsl(123, 70%, 50%)",
      burger: 91,
      burgerColor: "hsl(321, 70%, 50%)",
      sandwich: 88,
      sandwichColor: "hsl(171, 70%, 50%)",
      kebab: 49,
      kebabColor: "hsl(358, 70%, 50%)",
      fries: 184,
      friesColor: "hsl(16, 70%, 50%)",
      donut: 57,
      donutColor: "hsl(59, 70%, 50%)",
    },
    {
      country: "AM",
      "hot dog": 62,
      "hot dogColor": "hsl(137, 70%, 50%)",
      burger: 39,
      burgerColor: "hsl(356, 70%, 50%)",
      sandwich: 187,
      sandwichColor: "hsl(156, 70%, 50%)",
      kebab: 132,
      kebabColor: "hsl(172, 70%, 50%)",
      fries: 190,
      friesColor: "hsl(280, 70%, 50%)",
      donut: 91,
      donutColor: "hsl(91, 70%, 50%)",
    },
  ];

  return (
    <div style={{ width: "1280px", height: "950px" }}>
      <ResponsiveBar
        data={data}
        keys={["hot dog", "burger", "sandwich", "kebab", "fries", "donut"]}
        indexBy="country"
        margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
        padding={0.3}
        colors={{ scheme: "nivo" }}
        defs={[
          {
            id: "dots",
            type: "patternDots",
            background: "inherit",
            color: "#38bcb2",
            size: 4,
            padding: 1,
            stagger: true,
          },
          {
            id: "lines",
            type: "patternLines",
            background: "inherit",
            color: "#eed312",
            rotation: -45,
            lineWidth: 6,
            spacing: 10,
          },
        ]}
        fill={[
          {
            match: {
              id: "fries",
            },
            id: "dots",
          },
          {
            match: {
              id: "sandwich",
            },
            id: "lines",
          },
        ]}
        borderColor={{ from: "color", modifiers: [["darker", 1.6]] }}
        axisTop={null}
        axisRight={null}
        axisBottom={{
          tickSize: 5,
          tickPadding: 5,
          tickRotation: 0,
          legend: "country",
          legendPosition: "middle",
          legendOffset: 32,
        }}
        axisLeft={{
          tickSize: 5,
          tickPadding: 5,
          tickRotation: 0,
          legend: "food",
          legendPosition: "middle",
          legendOffset: -40,
        }}
        labelSkipWidth={12}
        labelSkipHeight={12}
        labelTextColor={{ from: "color", modifiers: [["darker", 1.6]] }}
        legends={[
          {
            dataFrom: "keys",
            anchor: "bottom-right",
            direction: "column",
            justify: false,
            translateX: 120,
            translateY: 0,
            itemsSpacing: 2,
            itemWidth: 100,
            itemHeight: 20,
            itemDirection: "left-to-right",
            itemOpacity: 0.85,
            symbolSize: 20,
            effects: [
              {
                on: "hover",
                style: {
                  itemOpacity: 1,
                },
              },
            ],
          },
        ]}
        animate={true}
        motionStiffness={90}
        motionDamping={15}
      />
    </div>
  );
};

render(<App />, document.querySelector("#content"));
