import React, { useEffect, useState } from "react";
import { render } from "react-dom";
import { ResponsiveBar } from "@nivo/bar";

const App = () => {
  const data = [
    {
      country: "topic1",
      Python: 3265.466646,
      "Py col": "hsl(88, 70%, 50%)",
      JavaScript: 2849.529511,
      JScol: "hsl(0, 70%, 50%)",
      Ruby: 2345.110145,
      Rubycol: "hsl(43, 70%, 50%)",
      Rails: 1935.50735,
      Railscol: "hsl(305, 70%, 50%)",
      PHP: 1692.440129,
      PHPcol: "hsl(95, 70%, 50%)",
      AWS: 1507.986289,
      AWScol: "hsl(11, 70%, 50%)",
    },
    {
      country: "topic2",
      Python: 3454.392643,
      "Py col": "hsl(54, 70%, 50%)",
      JavaScript: 3069.268382,
      JScol: "hsl(256, 70%, 50%)",
      Ruby: 2493.783734,
      Rubycol: "hsl(233, 70%, 50%)",
      Rails: 2046.870297,
      Railscol: "hsl(137, 70%, 50%)",
      PHP: 1814.98908,
      PHPcol: "hsl(90, 70%, 50%)",
      AWS: 1586.2199,
      AWScol: "hsl(248, 70%, 50%)",
    },
    {
      country: "topic3",
      Python: 2183.721489,
      "Py col": "hsl(182, 70%, 50%)",
      JavaScript: 1952.175319,
      JScol: "hsl(188, 70%, 50%)",
      Ruby: 1585.598742,
      Rubycol: "hsl(179, 70%, 50%)",
      Rails: 1309.425346,
      Railscol: "hsl(306, 70%, 50%)",
      PHP: 1153.927151,
      PHPcol: "hsl(161, 70%, 50%)",
      AWS: 1008.832482,
      AWScol: "hsl(4, 70%, 50%)",
    },
    {
      country: "topic4",
      Python: 2803.927262,
      "Py col": "hsl(278, 70%, 50%)",
      JavaScript: 2402.581309,
      JScol: "hsl(191, 70%, 50%)",
      Ruby: 1908.800861,
      Rubycol: "hsl(299, 70%, 50%)",
      Rails: 1629.334107,
      Railscol: "hsl(166, 70%, 50%)",
      PHP: 1395.031902,
      PHPcol: "hsl(47, 70%, 50%)",
      AWS: 1274.988517,
      AWScol: "hsl(126, 70%, 50%)",
    },
    {
      country: "topic5",
      Python: 2332.043779,
      "Py col": "hsl(324, 70%, 50%)",
      JavaScript: 2062.199988,
      JScol: "hsl(302, 70%, 50%)",
      Ruby: 1644.76456,
      Rubycol: "hsl(34, 70%, 50%)",
      Rails: 1359.006042,
      Railscol: "hsl(120, 70%, 50%)",
      PHP: 1230.900044,
      PHPcol: "hsl(45, 70%, 50%)",
      AWS: 1063.623316,
      AWScol: "hsl(276, 70%, 50%)",
    },
    {
      country: "topic6",
      Python: 2959.207302,
      "Py col": "hsl(123, 70%, 50%)",
      JavaScript: 2619.573798,
      JScol: "hsl(321, 70%, 50%)",
      Ruby: 2072.047838,
      Rubycol: "hsl(171, 70%, 50%)",
      Rails: 1745.179099,
      Railscol: "hsl(358, 70%, 50%)",
      PHP: 1532.344598,
      PHPcol: "hsl(16, 70%, 50%)",
      AWS: 1343.401019,
      AWScol: "hsl(59, 70%, 50%)",
    },
    {
      country: "topic7",
      Python: 3526.143802,
      "Py col": "hsl(137, 70%, 50%)",
      JavaScript: 3040.609421,
      JScol: "hsl(356, 70%, 50%)",
      Ruby: 2407.085526,
      Rubycol: "hsl(156, 70%, 50%)",
      Rails: 2027.605787,
      Railscol: "hsl(172, 70%, 50%)",
      PHP: 1764.452068,
      PHPcol: "hsl(280, 70%, 50%)",
      AWS: 1621.335168,
      AWScol: "hsl(91, 70%, 50%)",
    },
  ];

  return (
    <div style={{ width: "1280px", height: "950px" }}>
      <ResponsiveBar
        data={data}
        keys={["Python", "JavaScript", "Ruby", "Rails", "PHP", "AWS"]}
        indexBy="country"
        margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
        padding={0.3}
        colors={{ scheme: "nivo" }}
        defs={[
          {
            id: "dots",
            type: "patternDots",
            background: "inherit",
            color: "#38bcb2",
            size: 4,
            padding: 1,
            stagger: true,
          },
          {
            id: "lines",
            type: "patternLines",
            background: "inherit",
            color: "#eed312",
            rotation: -45,
            lineWidth: 6,
            spacing: 10,
          },
        ]}
        fill={[
          {
            match: {
              id: "PHP",
            },
            id: "dots",
          },
          {
            match: {
              id: "Ruby",
            },
            id: "lines",
          },
        ]}
        borderColor={{ from: "color", modifiers: [["darker", 1.6]] }}
        axisTop={null}
        axisRight={null}
        axisBottom={{
          tickSize: 5,
          tickPadding: 5,
          tickRotation: 0,
          legend: "country",
          legendPosition: "middle",
          legendOffset: 32,
        }}
        axisLeft={{
          tickSize: 5,
          tickPadding: 5,
          tickRotation: 0,
          legend: "food",
          legendPosition: "middle",
          legendOffset: -40,
        }}
        labelSkipWidth={12}
        labelSkipHeight={12}
        labelTextColor={{ from: "color", modifiers: [["darker", 1.6]] }}
        legends={[
          {
            dataFrom: "keys",
            anchor: "bottom-right",
            direction: "column",
            justify: false,
            translateX: 120,
            translateY: 0,
            itemsSpacing: 2,
            itemWidth: 100,
            itemHeight: 20,
            itemDirection: "left-to-right",
            itemOpacity: 0.85,
            symbolSize: 20,
            effects: [
              {
                on: "hover",
                style: {
                  itemOpacity: 1,
                },
              },
            ],
          },
        ]}
        animate={true}
        motionStiffness={90}
        motionDamping={15}
      />
    </div>
  );
};

render(<App />, document.querySelector("#content"));
