import React, { useEffect, useState } from "react";
import { render } from "react-dom";
import { ResponsiveBar } from "@nivo/bar";

const App = () => {
  const data = [
    {
      country: "topic1",
      Python: 3265.466646,
      "Py col": "hsl(88, 70%, 50%)",
      JavaScript: 2849.529511,
      JScol: "hsl(0, 70%, 50%)",
      Ruby: 2345.110145,
      Rubycol: "hsl(43, 70%, 50%)",
      Rails: 28,
      Railscol: "hsl(305, 70%, 50%)",
      PHP: 63,
      PHPcol: "hsl(95, 70%, 50%)",
      AWS: 194,
      AWScol: "hsl(11, 70%, 50%)",
    },
    {
      country: "AE",
      Python: 4,
      "Py col": "hsl(54, 70%, 50%)",
      JavaScript: 95,
      JScol: "hsl(256, 70%, 50%)",
      Ruby: 150,
      Rubycol: "hsl(233, 70%, 50%)",
      Rails: 8,
      Railscol: "hsl(137, 70%, 50%)",
      PHP: 65,
      PHPcol: "hsl(90, 70%, 50%)",
      AWS: 38,
      AWScol: "hsl(248, 70%, 50%)",
    },
    {
      country: "AF",
      Python: 123,
      "Py col": "hsl(182, 70%, 50%)",
      JavaScript: 87,
      JScol: "hsl(188, 70%, 50%)",
      Ruby: 5,
      Rubycol: "hsl(179, 70%, 50%)",
      Rails: 51,
      Railscol: "hsl(306, 70%, 50%)",
      PHP: 137,
      PHPcol: "hsl(161, 70%, 50%)",
      AWS: 117,
      AWScol: "hsl(4, 70%, 50%)",
    },
    {
      country: "AG",
      Python: 0,
      "Py col": "hsl(278, 70%, 50%)",
      JavaScript: 1,
      JScol: "hsl(191, 70%, 50%)",
      Ruby: 84,
      Rubycol: "hsl(299, 70%, 50%)",
      Rails: 23,
      Railscol: "hsl(166, 70%, 50%)",
      PHP: 108,
      PHPcol: "hsl(47, 70%, 50%)",
      AWS: 99,
      AWScol: "hsl(126, 70%, 50%)",
    },
    {
      country: "AI",
      Python: 67,
      "Py col": "hsl(324, 70%, 50%)",
      JavaScript: 146,
      JScol: "hsl(302, 70%, 50%)",
      Ruby: 199,
      Rubycol: "hsl(34, 70%, 50%)",
      Rails: 195,
      Railscol: "hsl(120, 70%, 50%)",
      PHP: 51,
      PHPcol: "hsl(45, 70%, 50%)",
      AWS: 59,
      AWScol: "hsl(276, 70%, 50%)",
    },
    {
      country: "AL",
      Python: 93,
      "Py col": "hsl(123, 70%, 50%)",
      JavaScript: 91,
      JScol: "hsl(321, 70%, 50%)",
      Ruby: 88,
      Rubycol: "hsl(171, 70%, 50%)",
      Rails: 49,
      Railscol: "hsl(358, 70%, 50%)",
      PHP: 184,
      PHPcol: "hsl(16, 70%, 50%)",
      AWS: 57,
      AWScol: "hsl(59, 70%, 50%)",
    },
    {
      country: "AM",
      Python: 62,
      "Py col": "hsl(137, 70%, 50%)",
      JavaScript: 39,
      JScol: "hsl(356, 70%, 50%)",
      Ruby: 187,
      Rubycol: "hsl(156, 70%, 50%)",
      Rails: 132,
      Railscol: "hsl(172, 70%, 50%)",
      PHP: 190,
      PHPcol: "hsl(280, 70%, 50%)",
      AWS: 91,
      AWScol: "hsl(91, 70%, 50%)",
    },
  ];

  return (
    <div style={{ width: "1280px", height: "950px" }}>
      <ResponsiveBar
        data={data}
        keys={["Python", "JavaScript", "Ruby", "Rails", "PHP", "AWS"]}
        indexBy="country"
        margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
        padding={0.3}
        colors={{ scheme: "nivo" }}
        defs={[
          {
            id: "dots",
            type: "patternDots",
            background: "inherit",
            color: "#38bcb2",
            size: 4,
            padding: 1,
            stagger: true,
          },
          {
            id: "lines",
            type: "patternLines",
            background: "inherit",
            color: "#eed312",
            rotation: -45,
            lineWidth: 6,
            spacing: 10,
          },
        ]}
        fill={[
          {
            match: {
              id: "PHP",
            },
            id: "dots",
          },
          {
            match: {
              id: "Ruby",
            },
            id: "lines",
          },
        ]}
        borderColor={{ from: "color", modifiers: [["darker", 1.6]] }}
        axisTop={null}
        axisRight={null}
        axisBottom={{
          tickSize: 5,
          tickPadding: 5,
          tickRotation: 0,
          legend: "country",
          legendPosition: "middle",
          legendOffset: 32,
        }}
        axisLeft={{
          tickSize: 5,
          tickPadding: 5,
          tickRotation: 0,
          legend: "food",
          legendPosition: "middle",
          legendOffset: -40,
        }}
        labelSkipWidth={12}
        labelSkipHeight={12}
        labelTextColor={{ from: "color", modifiers: [["darker", 1.6]] }}
        legends={[
          {
            dataFrom: "keys",
            anchor: "bottom-right",
            direction: "column",
            justify: false,
            translateX: 120,
            translateY: 0,
            itemsSpacing: 2,
            itemWidth: 100,
            itemHeight: 20,
            itemDirection: "left-to-right",
            itemOpacity: 0.85,
            symbolSize: 20,
            effects: [
              {
                on: "hover",
                style: {
                  itemOpacity: 1,
                },
              },
            ],
          },
        ]}
        animate={true}
        motionStiffness={90}
        motionDamping={15}
      />
    </div>
  );
};

render(<App />, document.querySelector("#content"));
